import React from "react";
import { serviceDetails } from "./ServiceWiseDetails";
import "./Services.css";
import { Col, Container, Row } from "react-bootstrap";
import TrackVisibility from "react-on-screen";
import { isMobileDevice } from "../Home/Home";

const Services = (serviceName) => {
  const jsx = serviceDetails[serviceName.serviceName];
  console.log(serviceName.serviceName, jsx);
  // return <div className="services">{jsx}</div>;
  return (
    <section className="banner" id="home">
      <Container
        style={{
          display: "flex",
          ...(isMobileDevice && { flexDirection: "column" }),
        }}
      >
        <Row className="aligh-items-center">
          <Col xs={18} md={13} xl={14}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? "animate__animated animate__fadeIn" : ""
                  }
                >
                  <div className="services">{jsx}</div>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
        <Row
          style={{
            borderLeft: "1px solid black",
          }}
          className="aligh-items-center"
        >
          <Col xs={22} md={16} xl={17}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? "animate__animated animate__fadeIn" : ""
                  }
                >
                  <ul
                    style={{
                      width: "20rem",
                    }}
                    class="list-group"
                  >
                    <a
                      style={{
                        textDecoration: "none",
                        color: "black",
                      }}
                      href="/services/TaxAndRegulatory"
                    >
                      {" "}
                      <li
                        class={`list-group-item ${
                          serviceName.serviceName === "TaxAndRegulatory"
                            ? "active"
                            : ""
                        }`}
                        aria-current="true"
                      >
                        Tax And Regulatory
                      </li>
                    </a>

                    <li
                      class={`list-group-item ${
                        serviceName.serviceName === "AuditAndAssurance"
                          ? "active"
                          : ""
                      }`}
                    >
                      Audit And Assurance
                    </li>
                    <li
                      class={`list-group-item ${
                        serviceName.serviceName === "CorporateLawServices"
                          ? "active"
                          : ""
                      }`}
                    >
                      Corporate Law Services
                    </li>
                    <li
                      class={`list-group-item ${
                        serviceName.serviceName === "AdvisoryServices"
                          ? "active"
                          : ""
                      }`}
                    >
                      Advisory Services
                    </li>
                    <li
                      class={`list-group-item ${
                        serviceName.serviceName ===
                        "FinanceAndAccountingOutsourcingServices"
                          ? "active"
                          : ""
                      }`}
                    >
                      Finance And Accounting Outsourcing Services
                    </li>
                  </ul>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Services;
