import React from "react";
import { serviceDetails } from "./ServiceWiseDetails";
import "./Services.css";
import { Col, Container, Row } from "react-bootstrap";
import TrackVisibility from "react-on-screen";
import { isMobileDevice } from "../Home/Home";

const Services = (serviceName) => {
  const jsx = serviceDetails[serviceName.serviceName];
  console.log(serviceName.serviceName, jsx);
  // return <div className="services">{jsx}</div>;
  return (
    <section className="banner" id="home">
      <Container
        style={{
          display: "flex",
          ...(isMobileDevice && { flexDirection: "column" }),
        }}
      >
        <Row style={{
          borderr
        }} className="aligh-items-center">
          <Col xs={13} md={7} xl={8}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? "animate__animated animate__fadeIn" : ""
                  }
                >
                  <div className="services">{jsx}</div>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
        <Row className="aligh-items-center">
          <Col xs={32} md={26} xl={27}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div
                  className={
                    isVisible ? "animate__animated animate__fadeIn" : ""
                  }
                >
                  <ul
                    style={{
                      width: "20rem",
                    }}
                    class="list-group"
                  >
                    <li class="list-group-item active" aria-current="true">
                      An active item
                    </li>
                    <li class="list-group-item">A second item</li>
                    <li class="list-group-item">A third item</li>
                    <li class="list-group-item">A fourth item</li>
                    <li class="list-group-item">And a fifth one</li>
                  </ul>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Services;
