import React, { useState } from 'react';

const Dropdown = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleItemClick = (action) => {
    // Do something when an item is clicked (e.g., navigate, perform an action)
    console.log(`Clicked ${action}`);
    // For this example, I'm just logging the selected action
  };

  return (
    <div className="dropdown">
      <button className="dropdown-toggle" onClick={toggleDropdown}>
        Dropdown
      </button>
      {isOpen && (
        <div className="dropdown-menu">
          <button className="dropdown-item" onClick={() => handleItemClick('Action 1')}>
            Action 1
          </button>
          <button className="dropdown-item" onClick={() => handleItemClick('Action 2')}>
            Action 2
          </button>
          <button className="dropdown-item" onClick={() => handleItemClick('Action 3')}>
            Action 3
          </button>
        </div>
      )}
    </div>
  );
};

export default Dropdown;
