import React, { useEffect, useState } from "react";
import "./Admin.css"; // Import CSS file for styling (create this file)
import { Link } from "react-router-dom";
import axios from "axios";

const Admin = (isAdminLoggedIn) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    // Perform login authentication here (e.g., send data to backend)
    if (username === "iamadmin" && password === "iwanttologin") {
      isAdminLoggedIn.setIsAdminLoggedIn(true);
    }
    alert("Hi admin, welcome!!");
    // Add your authentication logic here
  };

  const [queries, setQueries] = useState([{}]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/queries")
      .then((result) => {
        setQueries(result.data);
      })
      .catch((err) => {
        alert("Oops, something went wrong!");
        console.log(err);
      });
  }, []);

  const [applications, setApplications] = useState([{}]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/career-applications")
      .then((result) => {
        setApplications(result.data);
      })
      .catch((err) => {
        alert("Oops, something went wrong!");
      });
  }, []);

  console.log(applications);

  console.log(isAdminLoggedIn, isAdminLoggedIn);

  return !isAdminLoggedIn.isAdminLoggedIn ? (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Admin Login</h2>
        <div className="input-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" onClick={handleLogin}>
          Login
        </button>
      </form>
    </div>
  ) : (
    <div className="queries-and-applications-wrapper">
      <div>
        <h2>Welcome Admin, hope you are having a good day!</h2>
        <h3>Queries List</h3>
        <div className="queries-list">
          {" "}
          {queries.map((query, index) => {
            return (
              <div className="query-item">
                <div>Query Id: {index}</div>
                <div>Name: {query.name}</div>
                <div>Designation: {query.designation}</div>
                <div>Organization: {query.organization}</div>
                <div>Office Address: {query.officeAddress}</div>
                <div>City: {query.city}</div>
                <div>Email Address: {query.emailAddress}</div>
                <div>Telephone No.: {query.telephoneNo}</div>
                <div>Mobile No.: {query.mobileNo}</div>
                <div>Professional Updates: {query.professionalUpdates}</div>
                <div>Query Subject: {query.querySubject}</div>
                <div>Query: {query.query}</div>
              </div>
            );
          })}
        </div>
      </div>
      <div>
        <h3>Applications List</h3>
        <div className="queries-list">
          {" "}
          {applications.map((query, index) => {
            return (
              <div className="query-item">
                <div>Application Number: {index}</div>
                <div>First Name: {query.firstName}</div>
                <div>Last Name: {query.lastName}</div>
                <div>Email: {query.email}</div>
                <div>Mobile No.: {query.mobileNumber}</div>
                <div>Gender: {query.gender}</div>
                <div>Position: {query.position}</div>
                <div>Date Of Birth: {query.dob}</div>
                <div>Highest Qualification: {query.highestQualification}</div>
                <div>Resume: {query.resume}</div>
                <div>Last Working Company: {query.lastWorkedCompany}</div>
                <div>Experience Years: {query.query}</div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Admin;
