import React, { useState } from "react";
import './Dropdown.css';

export const Dropdown = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleItemClick = (action) => {
    // Do something when an item is clicked (e.g., navigate, perform an action)
    // For this example, I'm just logging the selected action
  };

  return (
    <div className="dropdown">
      <div className="dropdown-toggle" onClick={toggleDropdown}>
        Services
      </div>
      {isOpen && (
        <div className="dropdown-menu">
          <a
            className="dropdown-item"
            onClick={() => handleItemClick("Action 1")}
          >
            Tax and Regulatory
          </a>
          <a
            className="dropdown-item"
            onClick={() => handleItemClick("Action 2")}
          >
            Audit and Assurance
          </a>
          <a
            className="dropdown-item"
            onClick={() => handleItemClick("Action 3")}
          >
            Corporate Law Services
          </a>
        </div>
      )}
    </div>
  );
};

export default Dropdown;
